const Header = (props) => {
  return(
    <h1>
      {props.course}
    </h1>
  )
}

const Content = (props) => {
  return(
  <div>
    <div>
      <p> 
        <Part content={props.content[0]} />
      </p> 
    </div>
  </div>
  )
}

const Part = (props) => {
  return(
    <div>
      <p>
        {props.parts.name} {props.parts.exercises}
      </p>
    </div>
  )
}

const Total = (props) => {
