const initialState = {
    good: 0,
    ok: 0,
    bad: 0
  }

  const counterReducer = (state = [], action) => {
    console.log(action)
    switch (action.type) {
      case 'GOOD':
        return state + 1
      case 'OK':
        return state + 1
      case 'BAD':
        return state + 1
      case 'ZERO':
        return 0
      default: return state
    }
}

export const addGood = (state) => {
    return {
      type: 'GOOD',
      payload: {
        state
        }
    }
  }

  export default counterReducer
