import { useSelector, useDispatch } from 'react-redux';

import { createStore } from 'redux';

const initialState = {
  good: 0,
  ok: 0,
  bad: 0
};

const counterReducer = (state = initialState, action) => {
  switch (action.type) {
    case 'GOOD':
      return { ...state, good: state.good + 1 };
    case 'OK':
      return { ...state, ok: state.ok + 1 };
    case 'BAD':
      return { ...state, bad: state.bad + 1 };
    case 'ZERO':
      return initialState;
    default:
      return state;
  }
};

const store = createStore(counterReducer);

const App = () => {
  const dispatch = useDispatch();
  const goodCount = useSelector(state => state.good);
  const okCount = useSelector(state => state.ok);
  const badCount = useSelector(state => state.bad);

  return (
    <div>
      <button onClick={() => dispatch({ type: 'GOOD' })}>Good</button>
      <button onClick={() => dispatch({ type: 'OK' })}>ok</button> 
      <button onClick={() => dispatch({ type: 'BAD' })}>bad</button>
      <button onClick={() => dispatch({ type: 'ZERO' })}>reset stats</button>
      <div>good {goodCount}</div>
      <div>ok {okCount}</div>
      <div>bad {badCount}</div>
    </div>
  );
};

export default App;
