const initialState = {
    good: 0,
    ok: 0,
    bad: 0
  }

  const counterReducer = (state = [], action) => {
    console.log(action)
    switch (action.type) {
      case 'GOOD':
        return [...state, action.payload]
      case 'OK':
        return [...state, action.payload]
      case 'BAD':
        return [...state, action.payload]
      case 'ZERO':
        return [...state, action.payload]
      default: return state
    }
}

export const createNote = (content) => {
    return {
      type: 'NEW_NOTE',
      payload: {
        content,
